var isAnagram = function(strA, strB) { 
    const loweredA= strA.toLowerCase() 
    const loweredB= strB.toLowerCase() 
   const arrayA= Array.from(loweredA).sort() 
    const arrayB= Array.from(loweredB).sort() 
    const newA = arrayA.join() 
    const newB= arrayB.join() 
   return newA===newB 
 }; 

 const strA="battle" 
