var oddOrEven = function(num) { 
    if (num%2===0){ 
        console.log("even") 
    } 
    else{ 
        console.log("odd") 
    } 
 };
 oddOrEven(74)

var isPalindrome = function(str) { 
 const str1= str.toLowerCase() 
 const newArray= Array.from(str1).reverse().join("")
  return (str1===newArray)
 };
 console.log(isPalindrome('Palindrome'))

let count =[]
var countdown = function(num) {
for(let i=num; i>=0; i--){
count.push(i)
}
return count
};
 console.log(countdown(6))

const array =[1,2,3,4,5,5,5] 
 var sumArray = function(total, currentValue) { 
 return total + currentValue 
}; 
 console.log(array.reduce(sumArray))

var factorial = function(num) { 
   var count=1 
   for (let i=1; i<num+1; i++){ 
    count = count * i 
  } 
   return count 
 }; 

 console.log(factorial(5));

var titleCase = function(str) { 
   const trial=str.split(' ') 
   const loopT = trial.map(s=> s[0].toUpperCase()+ s.substr(1)).join(" ") 
 return loopT 
 }; console.log(titleCase("how is your day going"))
