def generate_permutations_naive(data):
  if len(data) == 0:
    return ['']
  permutations = []
  for i in range(len(data)):
    remaining = data[:i] + data[i+1:]
    for perm in generate_permutations_naive(remaining):
        permutations.append(data[i] + perm)
  return permutations

letters = "abc"
all_permutations = generate_permutations_naive(letters)
